mapper.select:
select id, taskNo, timeKeyValue, FROM_UNIXTIME(FLOOR(executeTime/1000),'%Y-%m-%d %H:%i:%s') as executeTime, taskStatus, failcount, TIME_FORMAT(TIMEDIFF(FROM_UNIXTIME(FLOOR(lastModifyTime/1000),'%Y-%m-%d %H:%i:%s'),FROM_UNIXTIME(FLOOR(createTime/1000),'%Y-%m-%d %H:%i:%s')),'%H:%i:%s') as time from quartz_task_records where taskNo = #{taskNo}
and taskStatus = #{taskStatus}
order by createTime desc limit #{currentPage},10
